import React, { Component } from "react";
import ItemGrid from "./ItemGrid";
import Sectiontitle from "../../component/Banner/Sectiontitle";
import anime from "animejs/lib/anime.es.js";
import CounterItem from "../Skill/CounterItem";
import Reveal from "react-reveal/Reveal/";

class portfolio extends Component {
  componentDidMount() {
    function countup(el, target) {
      let data = { count: 0 };
      anime({
        targets: data,
        count: [0, target],
        duration: 2000,
        round: 1,
        delay: 200,
        easing: "easeOutCubic",
        update() {
          el.innerText = data.count.toLocaleString();
        },
      });
    }

    function makeCountup(el) {
      const text = el.textContent;
      const target = parseInt(text, 10);

      const io = new IntersectionObserver((entries) => {
        entries.forEach((entry) => {
          if (entry.intersectionRatio > 0) {
            countup(el, target);
            io.unobserve(entry.target);
          }
        });
      });

      io.observe(el);
    }

    const els = document.querySelectorAll("[data-countup]");

    els.forEach(makeCountup);
  }
  render() {
    var { pClass } = this.props;
    var { cClass } = this.props;
    return (
      <>
        <section className={`portfolio_area ${pClass}`} id="portfolio">
          <div className="container">
            <Sectiontitle
              Title="My Portfolio"
              TitleP="I love what I do. I take great pride in what I do."
            />
            <Reveal effect="fadeInUp">
              <div className="row">
                <CounterItem
                  CText="200"
                  pdescription="Total Projects"
                  icon="briefcase"
                />
                <CounterItem
                  CText="120"
                  pdescription="Happy Clients"
                  icon="happy"
                />
                <CounterItem
                  CText="4"
                  pdescription="Years of Experience"
                  icon="calendar"
                />
                <CounterItem
                  CText="10"
                  pdescription="Award Won"
                  icon="trophy"
                />
              </div>
            </Reveal>
          </div>
        </section>
      </>
    );
  }
}

export default portfolio;
