import React, { Component } from "react";
import Reveal from "react-reveal/Reveal/";
import WaterWave from "react-water-wave";

export const RBanner = ({ jhonData, bClass, textLeft, imag }) => {
  return (
    <section id="home">
      <div className="container">
        <div className={`banner_content ${textLeft}`}>
          <Reveal effect="fadeInUp">
            <h5>HELLO</h5>
          </Reveal>
          <Reveal effect="fadeInUp" duration={1500}>
            <h2 className="wow fadeInLeft animated">I am {jhonData.name}</h2>
          </Reveal>
          <Reveal effect="fadeInUp" duration={2200}>
            <h4 className="wow fadeInUp anoimated">
              Visual Designer & Front-end Developer
            </h4>
          </Reveal>
          <ul className="list_style social_icon">
            {jhonData.socialLinks &&
              jhonData.socialLinks.map((item) => {
                return (
                  <li key={item.name}>
                    <a href={item.url}>
                      <i className={item.className}></i>
                    </a>
                  </li>
                );
              })}
          </ul>
        </div>
      </div>
    </section>
  );
};
